      for Default_Switches ("Ada") use ("-g", "-O0", "-gnat2022");
      for Switches ("Ada") use ("-gnata");
   end Compiler;

   package Builder is
      for Default_Switches ("Ada") use ("-g");
      for Switches ("Ada") use ("-gnata");
      for Global_Configuration_Pragmas use "main.adc";
   end Builder;

end Main;

